    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        Usuario usuario = new Usuario();
        usuario.setUsuario(txtUsuario.getText());
        usuario.setSenha(txtSenha.getText());

        usuario = UsuarioJPA.validarUsuario(usuario);

        if (usuario != null) {
            JOptionPane.showMessageDialog(null, "Usuario " + usuario.getTipo() + " autenticado com sucesso!");
            TelaInicial tela = new TelaInicial(usuario);
            tela.setVisible(true);
        } else {
            JOptionPane.showMessageDialog(null, "Usuario n√£o encontrado!");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

